import React, { useState, useEffect } from 'react';
import '../styles/ResumeUploadModal.css';

const ResumeUploadModal = ({ isOpen, onClose, onSave, fileName, defaultTitle = '' }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    if (isOpen) {
      // Set default title from fileName if provided
      if (defaultTitle) {
        setTitle(defaultTitle);
      } else if (fileName) {
        const nameWithoutExt = fileName.replace(/\.[^/.]+$/, '');
        setTitle(nameWithoutExt);
      } else {
        setTitle('');
      }
      setDescription('');
      setError('');
    }
  }, [isOpen, fileName, defaultTitle]);

  const handleSave = () => {
    if (!title || !title.trim()) {
      setError('Resume title is required');
      return;
    }

    onSave({
      title: title.trim(),
      description: description.trim()
    });
  };

  const handleClose = () => {
    setTitle('');
    setDescription('');
    setError('');
    onClose();
  };

  if (!isOpen) return null;

  return (
    <div className="resume-upload-modal-overlay" onClick={handleClose}>
      <div className="resume-upload-modal" onClick={(e) => e.stopPropagation()}>
        <div className="resume-upload-modal-header">
          <h2>ðŸ“„ Upload Resume</h2>
          <button className="resume-upload-modal-close" onClick={handleClose}>Ã—</button>
        </div>

        <div className="resume-upload-modal-content">
          {error && <div className="resume-upload-modal-error">{error}</div>}

          <div className="resume-upload-modal-form-group">
            <label htmlFor="resume-title">
              Resume Title <span className="required">*</span>
            </label>
            <input
              type="text"
              id="resume-title"
              value={title}
              onChange={(e) => {
                setTitle(e.target.value);
                setError('');
              }}
              placeholder="e.g., Software Engineer Resume 2024"
              className="resume-upload-modal-input"
              autoFocus
            />
            <p className="resume-upload-modal-hint">
              Give your resume a descriptive title to easily identify it later
            </p>
          </div>

          <div className="resume-upload-modal-form-group">
            <label htmlFor="resume-description">
              Additional Description <span className="optional">(Optional)</span>
            </label>
            <textarea
              id="resume-description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Add any additional notes or context about this resume..."
              className="resume-upload-modal-textarea"
              rows="4"
            />
            <p className="resume-upload-modal-hint">
              Optional: Add notes about this resume version, target roles, or any relevant information
            </p>
          </div>
        </div>

        <div className="resume-upload-modal-footer">
          <button className="resume-upload-modal-btn-cancel" onClick={handleClose}>
            Cancel
          </button>
          <button className="resume-upload-modal-btn-save" onClick={handleSave}>
            Save Resume
          </button>
        </div>
      </div>
    </div>
  );
};

export default ResumeUploadModal;
